package com.example.buku_tetangga.Items;public class Buku {    String rakbuku_id, judul_buku, harga, pengarang, penerbit, jumlah_stock, stock, foto;    public String getRakbuku_id(){ return rakbuku_id; }    public void setRakbuku_id(String rakbuku_id){ this.rakbuku_id = rakbuku_id; }    public String getFoto() {        return foto;    }    public void setFoto(String foto) {        this.foto = foto;    }    public String getJudul_buku() {        return judul_buku;    }    public void setJudul_buku(String judul_buku) {        this.judul_buku = judul_buku;    }    public String getHarga() {        return "Rp. "+harga;    }    public void setHarga(String harga) {        this.harga = harga;    }    public String getPengarang() {        return pengarang;    }    public void setPengarang(String pengarang) {        this.pengarang = pengarang;    }    public String getPenerbit() {        return penerbit;    }    public void setPenerbit(String penerbit) {        this.penerbit = penerbit;    }    public String getJumlah_stock() {        return jumlah_stock;    }    public void setJumlah_stock(String jumlah_stock) {        this.jumlah_stock = jumlah_stock;    }    public String getStock() {        return stock;    }    public void setStock(String stock) {        this.stock = stock;    }}